import React from "react";

const ProductCard = ({ product_name, price, category, subcategory, details, link, product_images }) => {
  return (
    <div className="product-card-item">
      <a href={link} target="_blank" rel="noopener noreferrer" className="product-link">
        {/* üñºÔ∏è Image Gallery */}
        <div className="product-image-gallery">
          {product_images && product_images.length > 0 ? (
            product_images.map((url, idx) => (
              <img
                key={idx}
                src={url}   // local path
                alt={`${product_name} ${idx + 1}`}
                className="product-image"
                loading="lazy"
              />
            ))
          ) : (
            <p className="no-image">No image available</p>
          )}
        </div>

        {/* üìõ Product Info */}
        <h3>{product_name}</h3>
        <p className="subcategory">{subcategory}</p>
        <p className="price">{price}</p>
        <p className="category">{category}</p>
      </a>

      {/* üé® Inline CSS */}
      <style>{`
        .product-card-item { /* styles unchanged */ }
        .product-card-item:hover { /* styles unchanged */ }
        .product-link { /* styles unchanged */ }
        .product-image-gallery { display: flex; overflow-x: auto; gap: 10px; margin-bottom: 12px; scroll-behavior: smooth; }
        .product-image { width: 180px; height: 200px; object-fit: cover; border-radius: 10px; flex-shrink: 0; }
        .subcategory, .category { font-size: 0.85rem; color: #777; }
        .price { font-size: 1rem; color: #000; font-weight: 500; }
        .no-image { color: #999; font-size: 0.9rem; }
      `}</style>
    </div>
  );
};

export default ProductCard;
