import React from "react";

const ProductCard = ({ product_name, price, category, subcategory, product_images }) => {
  // Parse image list from the weird JSON string (replace single quotes and parse)
  let images = [];
  try {
    images = JSON.parse(product_images.replace(/'/g, '"'));
  } catch (e) {
    console.error("Error parsing images for:", product_name);
  }

  // Extract URLs from parsed image objects
  const imageUrls = images.map((imgObj) => Object.keys(imgObj)[0]);

  return (
    <div className="product-card-item">
      {/* Image Section */}
      <div className="product-image-gallery">
        {imageUrls.map((url, index) => (
          <img key={index} src={url} alt={`${product_name} ${index + 1}`} />
        ))}
      </div>

      {/* Text Section */}
      <h3>{product_name}</h3>
      <p className="subcategory">{subcategory}</p>
      <p className="price">{price}</p>
      <p className="category">{category}</p>

      <style>{`
        .product-card-item {
          flex: 1 1 calc(25% - 20px);
          background-color: #fff;
          border-radius: 12px;
          box-shadow: 0 2px 8px rgba(0,0,0,0.1);
          padding: 15px;
          text-align: center;
          transition: transform 0.2s ease;
        }

        .product-card-item:hover {
          transform: translateY(-5px);
        }

        .product-image-gallery {
          display: flex;
          overflow-x: auto;
          gap: 10px;
          scroll-behavior: smooth;
          margin-bottom: 10px;
        }

        .product-image-gallery img {
          width: 100%;
          max-width: 200px;
          height: 180px;
          object-fit: cover;
          border-radius: 10px;
          flex-shrink: 0;
        }

        h3 {
          font-size: 1rem;
          margin: 8px 0 5px;
        }

        .subcategory, .category {
          font-size: 0.9rem;
          color: #666;
          margin: 0;
        }

        .price {
          font-size: 1rem;
          font-weight: 600;
          color: #ff6f61;
          margin: 5px 0;
        }

        @media (max-width: 768px) {
          .product-card-item {
            flex: 1 1 calc(50% - 20px);
          }
        }

        @media (max-width: 480px) {
          .product-card-item {
            flex: 1 1 100%;
          }

          .product-image-gallery img {
            max-width: 100%;
          }
        }
      `}</style>
    </div>
  );
};

export default ProductCard;
