import React from "react";
import { Link } from "react-router-dom";

export default function ProductCard({
  id,
  product_name,
  price,
  images,
  is_available,
}) {
  const CardWrapper = is_available ? Link : "div";

  return (
    <CardWrapper
      to={is_available ? `/products/${id}` : undefined}
      className="card-link"
    >
      <div className={`product-card-item ${!is_available ? "disabled" : ""}`}>

        {/* IMAGE */}
        <div className="image-box">
          <img
            src={images[0]}
            alt={product_name}
            className={!is_available ? "blur" : ""}
          />

          {!is_available && (
            <span className="stock-badge">OUT OF STOCK</span>
          )}
        </div>

        {/* INFO */}
        <div className="card-info">
          <h4>{product_name}</h4>
          <p>â‚¹{price}</p>
        </div>
      </div>

      <style>{`
        .card-link {
          text-decoration: none;
          color: inherit;
          pointer-events: ${is_available ? "auto" : "none"};
        }

        .product-card-item {
          position: relative;
          background: #fff;
          border-radius: 16px;
          overflow: hidden;
          box-shadow: 0 6px 18px rgba(0,0,0,0.12);
          transition: all 0.3s ease;
        }

        .product-card-item.disabled {
          opacity: 0.6;
        }

        .image-box {
          position: relative;
          height: 260px;
          overflow: hidden;
        }

        .image-box img {
          width: 100%;
          height: 100%;
          object-fit: cover;
        }

        .image-box img.blur {
          filter: blur(3px) grayscale(40%);
        }

        .stock-badge {
          position: absolute;
          top: 12px;
          left: 12px;
          background: #000;
          color: #fff;
          padding: 6px 10px;
          font-size: 12px;
          border-radius: 6px;
          font-weight: 600;
        }

        .card-info {
          padding: 14px;
          text-align: center;
        }

        .card-info h4 {
          font-size: 16px;
          margin-bottom: 6px;
        }

        .card-info p {
          font-size: 15px;
          font-weight: 600;
        }
      `}</style>
    </CardWrapper>
  );
}
